import requests
from config import YANDEX_GPT_IDENTIFICATOR, YANDEX_GPT_API_KEY


def debug(resp):
    if isinstance(resp, dict):
        for key in resp:
            print(key)
            debug(resp[key])
    else:
        print(resp)


def creating_conspect(text, output_format):
    prompt = {
        "modelUri": f"gpt://{YANDEX_GPT_IDENTIFICATOR}/yandexgpt/latest",
        "completionOptions": {
            "stream": False,
            "temperature": 0.6,
            "maxTokens": "2000"
        },
        "messages": [
            {
                "role": "user",
                "text": "Необходимо написать подробный конспект этого текcта и формить его в формате " +
                        output_format + ". Можно вставлять картинки из интернета, где это будет уместно.\n" + text
            }
        ]
    }

    url = "https://llm.api.cloud.yandex.net/foundationModels/v1/completion"
    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Api-Key {YANDEX_GPT_API_KEY}"
    }

    response = requests.post(url, headers=headers, json=prompt).json()
    debug(response)


